<?phprequire_once 'userController.php';require_once 'database.php';$database = new Database();$databaseConnection = $database->getConnection();$userController = new UserController($databaseConnection);$name = "";$email = "";$message = "";if ($_SERVER["REQUEST_METHOD"] == "POST") {  $name = htmlspecialchars($_POST["name"]);  $email = htmlspecialchars($_POST["email"]);  $user = $userController->create($name, $email);  if ($user) {    $message = "Usuário registrado com sucesso.";  } else {    $message = "Falha ao registrar o usuário.";  }}echo json_encode(array("message" => $message));?>